import React, { useCallback, useEffect } from 'react';
import ReactDOM from 'react-dom';
import styles from './index.module.scss';

const iconMap = {
  success:
    'data:image/png;base64,',
  fail: 'data:image/png;base64,',
  loading:
    'data:image/gif;base64,',
};

function Toast({ title = '', icon = '', duration = 2000 }) {
  useEffect(() => {
    let timer = setTimeout(() => {
      timer = null;
      Toast.hideToast();
    }, duration);

    return () => {
      if (timer) {
        clearTimeout(timer);
        timer = null;
      }
    };
  }, [duration]);

  return (
    <div className={styles.wrap}>
      <div className={styles.content}>
        {iconMap[icon] ? <img className={styles.icon} src={iconMap[icon]} alt="图标" /> : null}
        <span>{title}</span>
      </div>
    </div>
  );
}

function Modal({
  title = '温馨提示',
  content = '',
  confirmColor = '#666',
  confirmText = '确认',
  showCancel = true,
  cancelColor = '#666',
  cancelText = '取消',
  ok,
  cancel,
}) {
  const onCancle = useCallback(() => {
    Toast.hideModal();

    cancel && cancel();
  }, [cancel]);

  const onOk = useCallback(() => {
    Toast.hideModal();

    ok && ok();
  }, [ok]);

  return (
    <div className={styles.wrap} style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
      <div className={styles.modalContent}>
        <div className={styles.header}>
          {title ? <span className={styles.title}>{title}</span> : null}
          {content ? <span className={styles.word}>{content}</span> : null}
        </div>
        <div className={styles.btnWrap}>
          {showCancel ? (
            <div className={styles.btn} style={{ color: cancelColor }} onClick={onCancle}>
              {cancelText}
            </div>
          ) : null}
          <div className={styles.btn} style={{ color: confirmColor }} onClick={onOk}>
            {confirmText}
          </div>
        </div>
      </div>
    </div>
  );
}

function Loading({ title = '', isMask = true }) {
  return (
    <div className={styles.wrap} style={{ pointerEvents: isMask ? 'auto' : 'none' }}>
      <div className={styles.loadingContent}>
        <img className={styles.icon} src={iconMap.loading} alt="加载图标" />
        {title ? <span className={styles.title}>{title}</span> : null}
      </div>
    </div>
  );
}

Toast.showToast = (() => {
  let div = null;
  let Cmp = null;

  Toast.hideToast = () => {
    if (div) {
      ReactDOM.unmountComponentAtNode(div);
      document.body.removeChild(div);
      div = null;
      Cmp = null;
    }
  };

  return (props = {}) => {
    Toast.hideToast();

    div = document.createElement('div');

    document.body.appendChild(div);

    Cmp = <Toast {...props} />;

    ReactDOM.render(Cmp, div);
  };
})();

Toast.showModal = (() => {
  let div = null;
  let Cmp = null;

  Toast.hideModal = () => {
    if (div) {
      ReactDOM.unmountComponentAtNode(div);
      document.body.removeChild(div);
      div = null;
      Cmp = null;
    }
  };

  return (props = {}) => {
    Toast.hideModal();

    div = document.createElement('div');

    document.body.appendChild(div);

    Cmp = <Modal {...props} />;

    ReactDOM.render(Cmp, div);
  };
})();

Toast.showLoading = (() => {
  let div = null;
  let Cmp = null;

  Toast.hideLoading = () => {
    if (div) {
      ReactDOM.unmountComponentAtNode(div);
      document.body.removeChild(div);
      div = null;
      Cmp = null;
    }
  };

  return (props = {}) => {
    Toast.hideLoading();

    div = document.createElement('div');

    document.body.appendChild(div);

    Cmp = <Loading {...props} />;

    ReactDOM.render(Cmp, div);
  };
})();

export default Toast;
